import React, { useState } from "react";
import { Link } from "react-router-dom";
import WineSelectRow from "./WineSelectRow"
import wineTypes from "../wineTypeData";


const MenuWine = ({
  handleWineSelect,
  selectionInfo,
  pairingFactor1,
  pairingFactor2,
  handleShowMeClick,
}) => {
  return (
    <div className="menu-wine">
      <h2>Select a wine category:</h2>
      {Object.keys(wineTypes).map((type, index) => {
        console.log(wineTypes[type])
        return (
          <WineSelectRow
            // handleWineSelect={handleWineSelect}
            // name={wineTypes.type.name}
            // bottle={wineTypes.type.bottletype}
            // subtypes={wineTypes.type.subtypes.join(", ")}
            // data-id={type}
          />
        )
      })}

      <div
        className="bottle-select bordeaux"
        onClick={handleWineSelect}
        data-id="boldred"
      >
        <h3>Bold Red</h3>
        <p class="subtype-list">{wineTypes.boldred.subtypes.join(", ")}</p>
      </div>
      <div
        className="bottle-select burgundy"
        onClick={handleWineSelect}
        data-id="mediumred"
      >
        <h3>Medium Red</h3>
        <p class="subtype-list">{wineTypes.mediumred.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select burgundy"
        onClick={handleWineSelect}
        data-id="lightred"
      >
        <h3>Light Red</h3>
        <p class="subtype-list">{wineTypes.lightred.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select bordeaux"
        onClick={handleWineSelect}
        data-id="rose"
      >
        <h3>Rosé</h3>
        <p class="subtype-list">{wineTypes.rose.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select burgundy"
        onClick={handleWineSelect}
        data-id="richwhite"
      >
        <h3>Rich White</h3>
        <p class="subtype-list">{wineTypes.richwhite.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select bordeaux"
        onClick={handleWineSelect}
        data-id="lightwhite"
      >
        <h3>Light White</h3>
        <p class="subtype-list">{wineTypes.lightwhite.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select sparkling"
        onClick={handleWineSelect}
        data-id="sparkling"
      >
        <h3>Sparkling</h3>
        <p class="subtype-list">{wineTypes.sparkling.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select bordeaux"
        onClick={handleWineSelect}
        data-id="sweetwhite"
      >
        <h3>Sweet</h3>
        <p class="subtype-list">{wineTypes.sweetwhite.subtypes.join(", ")}</p>

      </div>
      <div
        className="bottle-select port"
        onClick={handleWineSelect}
        data-id="dessert"
      >
        <h3>Dessert</h3>
        <p class="subtype-list">{wineTypes.dessert.subtypes.join(", ")}</p>

      </div>

      <ul className="wine-menu-varietal-list">
        {selectionInfo
          ? selectionInfo.subtypes.map((subtype, index) => (
              <li key={index}>{subtype}</li>
            ))
          : ""}
      </ul>
      {pairingFactor1 ? (
        <p>
          Will search for recipes containing {pairingFactor1} and {pairingFactor2}
        </p>
      ) : (
        ""
      )}

      <Link to="/reciperec" onClick={handleShowMeClick}>
        <p>Show me a recipe!</p>
      </Link>
    </div>
  );
};

export default MenuWine;
