import React, { useState } from "react";
import { Route, Link, Switch } from "react-router-dom";
import Welcome from "./Components/Welcome";
import ModeSelect from "./Components/ModeSelect";
import MenuWine from "./Components/MenuWine";
import MenuFood from "./Components/MenuFood";
import "./App.css";

function App() {
  const [selectedWine, setSelectedWine] = useState(""); //the wine the user selects
  const [recipeRec, setRecipeRec] = useState(""); //the recipe(s) we'll recommend

  const handleWineSelect = (wineType) => {
    console.log("Setting selected wine to " + wineType)
    setRecipeRec(wineType);
  }

  return (
    <div className="App">
      <nav></nav>
      <div className="main">
        <Switch>
          <Route exact path="/" component={Welcome} />
          <Route path="/modeselect" component={ModeSelect} handleWineSelect={handleWineSelect}/>
          <Route path="/menuwine" component={MenuWine} />
          <Route path="/menufood" component={MenuFood} />
        </Switch>
      </div>
    </div>
  );
}

export default App;
