import React, { useState } from "react";
import { Link } from "react-router-dom";

const MenuWine = () => {
  const [selectedWine, setSelectedWine] = useState(""); //the wine the user selects
  const [recipeUrl, setRecipeUrl] = useState("");
  const [recipeRec, setRecipeRec] = useState(""); //the recipe(s) we'll recommend
  const [pairingFactor1, setPairingFactor1] = useState("");
  const [pairingFactor2, setPairingFactor2] = useState("");

  const handleClick = event => {
    setSelectedWine(event.target.dataset.id);
  }

  console.log("wine = " + selectedWine);

  const handleShowMeClick = () => {
    console.log("showme click!")
    console.log("recipeUrl = " + recipeUrl)
    //actually make the api call
    const makeApiCall = async () => {
      console.log("Making API Call!!! No more than 5 per hour!")
      const response = await fetch(recipeUrl);
      const json = await response.json();
      setRecipeRec(json);
      console.log(recipeRec)
    };
    makeApiCall(); //exceeded my limit! wait to re-establish this.
  };

  //listen for a change in selectedWine, set the search url.
  useEffect(() => {
    console.log("useEffect!")
    //if a wine has been selected,
    if (selectedWine) {
      //get wine pairing array
      let pairingArray = wineTypes[selectedWine].pairingArray;
      //get array of food categories
      let foodTypeArray = Object.keys(foodTypes);
      //create array of arrays of food subcategories
      let foodSubtypeArray = [];
      foodTypeArray.forEach((type) => {
        foodSubtypeArray.push(Object.keys(foodTypes[type]));
      });
      //flatten the array of arrays into one big array
      let flatArray = foodSubtypeArray.flat();
      // console.log("foodTypeArray = " + foodTypeArray);
      // console.log(flatArray);
      //create an array with only foods with a value of "2" from pairing array (great pairs)
      //for now, we're being more general, so "pork" rather than "roast, tenderloin" etc.
      let masterArray = [];
      for (let i = 0; i < flatArray.length; i++) {
        if (pairingArray[i] === 2) {
          masterArray.push(flatArray[i]);
        }
      }
      // console.log(masterArray)
      // select two random indices from the master array
      let num1 = Math.floor(Math.random() * (masterArray.length / 2));
      let num2 = Math.floor((Math.random() * (masterArray.length / 2)) + (masterArray.length / 2));

      console.log(num1, num2);
      // TO DO: sparkling and rose only have one "2" apiece!

      // make our selections based on these indices

      let localPairFactor1 = masterArray[num1];
      let localPairFactor2 = masterArray[num2];

      console.log("local factor 1 = " + localPairFactor1 + " local factor 2 = " + localPairFactor2)


      setRecipeUrl(`https://api.edamam.com/search?q=${localPairFactor1}%20AND%20${localPairFactor2}&app_id=d9740b8f&app_key=ef3b8ea5fd0b0bffed8b9bc13e135c91`);
      // console.log("recipeURL = " + recipeUrl);
      setPairingFactor1(localPairFactor1);
      setPairingFactor2(localPairFactor2);
    }
  }, [selectedWine]);



    return (
    <div className="menu-wine">
        <p>Select a wine:</p>
        <div className="bottle-select" onClick={handleClick} data-id="boldred"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/>Bold Red</div>
        <div className="bottle-select" onClick={handleClick} data-id="mediumred"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/>Medium Red</div>
        <div className="bottle-select" onClick={handleClick} data-id="lightred"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/>Light Red</div>
        <div className="bottle-select" onClick={handleClick} data-id="rose"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/>Rosé</div>
        <div className="bottle-select" onClick={handleClick} data-id="richwhite"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/>Rich White</div>
        <div className="bottle-select" onClick={handleClick} data-id="lightwhite"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/>Light White</div>
        <div className="bottle-select" onClick={handleClick} data-id="sparkling"><img className="bottle-horizontal" src={require("../assets/bottle-sparkling-silhouette-white.png")}  alt="horizontal bottle"/>Sparkling</div>
        <div className="bottle-select" onClick={handleClick} data-id="sweet"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/>Sweet</div>
        <div className="bottle-select" onClick={handleClick} data-id="dessert"><img className="bottle-horizontal" src={require("../assets/bottle-port-silhouette-white.png")}  alt="horizontal bottle"/>Dessert</div>

      {/* <form>
        <select onChange={handleWineSelect}>
          <option value="boldred">Bold Red</option>
          <option value="mediumred">Medium Red</option>
          <option value="lightred">Light Red</option>
          <option value="rose">Rosé</option>
          <option value="richwhite">Rich White</option>
          <option value="lightwhite">Light White</option>
          <option value="sparkling">Sparkling</option>
          <option value="sweetwhite">Sweet White</option>
          <option value="dessert">Dessert</option>
        </select>
          </form> */}
          <ul className="wine-menu-varietal-list">
              {selectionInfo ? selectionInfo.subtypes.map((subtype, index) => <li key={index}>{subtype}</li>) : ""}
          </ul>
            {pairingFactor1 ? <p>Searching for recipes containing {pairingFactor1} and {pairingFactor2}</p> : ""}

        <Link to="/reciperec" onClick={handleShowMeClick}>
        <p>Show me a recipe!</p>
      </Link>
    </div>
  );
};

export default MenuWine;
