import React, { useState } from "react";
import { Link } from "react-router-dom";

const MenuWine = ({ handleWineSelect, selectionInfo, pairingFactor1, pairingFactor2, handleShowMeClick }) => {

    return (
    <div className="menu-wine">
        <p>Select a wine:</p>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="boldred">
          {/* <img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")} alt="horizontal bottle" /> */}
          <h3>Bold Red</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="mediumred"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/><h3>Medium Red</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="lightred"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/><h3>Light Red</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="rose"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/><h3>Ros√©</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="richwhite"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")}  alt="horizontal bottle"/><h3>Rich White</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="lightwhite"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/><h3>Light White</h3></div>
        <div className="bottle-select sparkling" onClick={handleWineSelect} data-id="sparkling"><img className="bottle-horizontal" src={require("../assets/bottle-sparkling-silhouette-white.png")}  alt="horizontal bottle"/><h3>Sparkling</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="sweetwhite"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")}  alt="horizontal bottle"/><h3>Sweet</h3></div>
        <div className="bottle-select port" onClick={handleWineSelect} data-id="dessert"><img className="bottle-horizontal" src={require("../assets/bottle-port-silhouette-white.png")}  alt="horizontal bottle"/><h3>Dessert</h3></div>

        <ul className="wine-menu-varietal-list">
              {selectionInfo ? selectionInfo.subtypes.map((subtype, index) => <li key={index}>{subtype}</li>) : ""}
          </ul>
            {pairingFactor1 ? <p>Searching for recipes containing {pairingFactor1} and {pairingFactor2}</p> : ""}

        <Link to="/reciperec" onClick={handleShowMeClick}>
        <p>Show me a recipe!</p>
      </Link>
    </div>
  );
};

export default MenuWine;
