import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import Welcome from "./Components/Welcome";
import ModeSelect from "./Components/ModeSelect";
import MenuWine from "./Components/MenuWine";
import MenuFood from "./Components/MenuFood";
import RecipeRec from "./Components/RecipeRec";
import wineTypes from "./wineTypeData";
import foodTypes from "./foodTypeData";
import "./App.css";

function App() {
  const [selectedWine, setSelectedWine] = useState(""); //the wine the user selects
  const [recipeRec, setRecipeRec] = useState(""); //the recipe(s) we'll recommend

  const handleWineSelect = (event) => {
    // console.log(event.target.value)
    setSelectedWine(event.target.value);
    // console.log("selected wine = " + selectedWine);
    // console.log(wineTypes.selectedWine);
  };

  //listen for a change in selectedWine, make an api call and set recipeRec
  useEffect(() => {
    const recipeUrl = "https://api.edamam.com/search?q=beef%20AND%20barbecued&20AND&20black&20pepper&app_id=c2605b0c&app_key=df18bda11a55f9351b07d5b7cff7c96b&from=0&to=3";
    const makeApiCall = async () => {
      const response = await fetch(recipeUrl);
      const json = await response.json();
      setRecipeRec(json)
    }
    makeApiCall()
  }, [selectedWine])

  // console.log(wineTypes[selectedWine]);
  // console.log(recipeRec);

  return (
    <div className="App">
      <nav></nav>
      <div className="main">
        <Switch>
          <Route exact path="/" component={Welcome} />
          <Route path="/modeselect" component={ModeSelect} />
          <Route
            path="/menuwine"
            render={(routerProps) => (
              <MenuWine handleWineSelect={handleWineSelect} selectionInfo={wineTypes[selectedWine]} {...routerProps} />
            )}
          />
          <Route
            path="/reciperec"
            render={(routerProps) => <RecipeRec recipeRec={recipeRec} {...routerProps} />}
          />
          <Route path="/menufood" component={MenuFood} />
        </Switch>
      </div>
    </div>
  );
}

export default App;
