import React from "react";
import { Link } from "react-router-dom";

const RecipeRec = ({ recipeRec, factor1, factor2, selectionInfo }) => {
  if (recipeRec.hits) {
    //check if one of the factors is a prep method. if so, modify that pairing factor to include "is" for grammatical reasons later.
    const preps = [
      "grilled",
      "barbecued",
      "sauteed",
      "fried",
      "smoked",
      "roasted",
      "steamed",
    ];
    //check factor1 first
    if (preps.includes(factor1)) {
      const factor1withGrammar = "is " + factor1;
    } else {
      const factor1withGrammar = "has " + factor1;
    }
    //then factor2
    if (preps.includes(factor2)) {
      const factor2withGrammar = "is " + factor2;
    } else {
      const factor2withGrammar = "has " + factor2;
    }

    return (
      <div className="recipe-rec">
        <div className="recipe-image-container">
          <img
            src={recipeRec.hits[0].recipe.image}
            alt="first recipe"
            key="0"
          />
        </div>
        <h3>{recipeRec.hits[0].recipe.label}</h3>
        <p>
          This recipe {factor1withGrammar} and {factor2withGrammar}, which pair well with your{" "}
          {selectionInfo.name}
        </p>
        <Link to="/">Back to Home.</Link>
      </div>
    );
  } else {
    return (
      <div className="recipe-rec">
        <p>Nothing yet.</p>
        <Link to="/">Back to Home.</Link>
      </div>
    );
  }
};

export default RecipeRec;
