import React, { useState } from "react";
import { Route, Link, Switch } from "react-router-dom";
import Welcome from "./Components/Welcome";
import ModeSelect from "./Components/ModeSelect";
import MenuWine from "./Components/MenuWine";
import RecipeRec from "./Components/RecipeRec";
import "./App.css";

function App() {
  const [selectedWine, setSelectedWine] = useState(""); //the wine the user selects
  const [recipeRec, setRecipeRec] = useState(""); //the recipe(s) we'll recommend

  return (
    <div className="App">
      <nav>
        <Link to="/"><p>Welcome</p></Link>
        <Link to="/modeselect"><p>ModeSelect</p></Link>
        <Link to="/menuwine"><p>MenuWine</p></Link>
        <Link to="/reciperec"><p>RecipeRec</p></Link>
      </nav>
      <Switch>
        <Route path="/" component={Welcome} />
        <Route path="/modeselect" component={ModeSelect} />
        <Route path="/menuwine" component={MenuWine} />
        <Route path="/reciperec" component={RecipeRec} />
      </Switch>
    </div>
  );
}

export default App;
