import React, { useState } from "react";
import { Link } from "react-router-dom";

const MenuWine = ({ handleWineSelect, selectionInfo, pairingFactor1, pairingFactor2, handleShowMeClick }) => {
  const [wine, setWine] = useState("");

  const handleClick = event => {
    setWine()
  }

    return (
    <div className="menu-wine">
        <p>Select a wine:</p>
        <div className="bottle-select" onClick={handleClick} data-id="boldred"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")} />Bold Red</div>
        <div className="bottle-select" data-id="mediumred"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")} />Medium Red</div>
        <div className="bottle-select" data-id="lightred"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")} />Light Red</div>
        <div className="bottle-select" data-id="rose"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")} />Rosé</div>
        <div className="bottle-select" data-id="richwhite"><img className="bottle-horizontal" src={require("../assets/bottle-burgundy-silhouette-white.png")} />Rich White</div>
        <div className="bottle-select" data-id="lightwhite"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")} />Light White</div>
        <div className="bottle-select" data-id="sparkling"><img className="bottle-horizontal" src={require("../assets/bottle-sparkling-silhouette-white.png")} />Sparkling</div>
        <div className="bottle-select" data-id="sweet"><img className="bottle-horizontal" src={require("../assets/bottle-bordeaux-silhouette-white.png")} />Sweet</div>
        <div className="bottle-select" data-id="dessert"><img className="bottle-horizontal" src={require("../assets/bottle-port-silhouette-white.png")} />Dessert</div>

      <form>
        <select onChange={handleWineSelect}>
          <option value="boldred">Bold Red</option>
          <option value="mediumred">Medium Red</option>
          <option value="lightred">Light Red</option>
          <option value="rose">Rosé</option>
          <option value="richwhite">Rich White</option>
          <option value="lightwhite">Light White</option>
          <option value="sparkling">Sparkling</option>
          <option value="sweetwhite">Sweet White</option>
          <option value="dessert">Dessert</option>
        </select>
          </form>
          <ul className="wine-menu-varietal-list">
              {selectionInfo ? selectionInfo.subtypes.map((subtype, index) => <li key={index}>{subtype}</li>) : ""}
          </ul>
            {pairingFactor1 ? <p>Searching for recipes containing {pairingFactor1} and {pairingFactor2}</p> : ""}

        <Link to="/reciperec" onClick={handleShowMeClick}>
        <p>Show me a recipe!</p>
      </Link>
    </div>
  );
};

export default MenuWine;
