import React, { useState } from "react";
import { Link } from "react-router-dom";

const MenuWine = ({ handleWineSelect, selectionInfo, pairingFactor1, pairingFactor2, handleShowMeClick }) => {

    return (
    <div className="menu-wine">
        <p>Select a wine:</p>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="boldred">
          <h3>Bold Red</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="mediumred"><h3>Medium Red</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="lightred"><h3>Light Red</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="rose"><h3>Ros√©</h3></div>
        <div className="bottle-select burgundy" onClick={handleWineSelect} data-id="richwhite"><h3>Rich White</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="lightwhite"><h3>Light White</h3></div>
        <div className="bottle-select sparkling" onClick={handleWineSelect} data-id="sparkling"><h3>Sparkling</h3></div>
        <div className="bottle-select bordeaux" onClick={handleWineSelect} data-id="sweetwhite"><h3>Sweet</h3></div>
        <div className="bottle-select port" onClick={handleWineSelect} data-id="dessert"><h3>Dessert</h3></div>

        <ul className="wine-menu-varietal-list">
              {selectionInfo ? selectionInfo.subtypes.map((subtype, index) => <li key={index}>{subtype}</li>) : ""}
          </ul>
            {pairingFactor1 ? <p>Searching for recipes containing {pairingFactor1} and {pairingFactor2}</p> : ""}

        <Link to="/reciperec" onClick={handleShowMeClick}>
        <p>Show me a recipe!</p>
      </Link>
    </div>
  );
};

export default MenuWine;
